pragma solidity ^0.8.6;

interface IERC721Permissioned {

    event TransferForced(address indexed from, address indexed to, uint256 indexed tokenId);
    event DelegatedMinterSet(address indexed newDelegatedMinter);
    event Mint(address indexed to, uint256 indexed tokenId);
    event Burn(address indexed from, uint256 indexed tokenId);
    event Freeze(address indexed from, uint256 indexed tokenId);
    event FreezeBatch(address indexed from, uint256[] tokenIds);
    event Release(address indexed from, uint256 indexed tokenId);

    // force a transfer from the user to the to address irrepective of registry
    // TODO: Does this mean that the transfer will go even if both addresses aren't
    //      registered? If so, this is a security issue.
    function forcedTransfer(
        address _from,
        address _to,
        uint256 _amount,
        bytes calldata _data
    ) external returns (bool);

    function setDelegatedMinter(address _newDelegatedMinter) external returns (bool);
    function delegatedMinter() external view returns (address);

    function mint(address _to, bytes calldata data) external returns (uint256);
    function burn(uint256 _tokenId) external returns (bool);

    function freeze(uint256 tokenID) external;
    function release(uint256 tokenID) external;

}
