pragma solidity ^0.8.0;

import '../interfaces/INFT.sol';
import '../interfaces/IInvoicePool.sol';
import '../interfaces/IVABB.sol';
import '../interfaces/IUSDCPool.sol';
import '../interfaces/IVABI.sol';
import "../identity/IdentityStorage.sol";


library VabbleLib {
    using IdentitySystemStorage for IdentitySystemStorage.IdentitySystem;

    function setNFTContract(address _nftContract) internal {
        IdentitySystemStorage.IdentitySystem storage ds = IdentitySystemStorage.diamondStorage();
        ds.vabbleStorage.nft = _nftContract;
    }

    function setInvoicePoolContract(address _invoicePoolContract) internal {
        IdentitySystemStorage.IdentitySystem storage ds = IdentitySystemStorage.diamondStorage();
        ds.vabbleStorage.invoicePool = _invoicePoolContract;
    }

    function setVABBContract(address _vabbContract) internal {
        IdentitySystemStorage.IdentitySystem storage ds = IdentitySystemStorage.diamondStorage();
        ds.vabbleStorage.vabb = _vabbContract;
    }

    function setUSDCPoolContract(address _usdcPoolContract) internal {
        IdentitySystemStorage.IdentitySystem storage ds = IdentitySystemStorage.diamondStorage();
        ds.vabbleStorage.usdcPool = _usdcPoolContract;
    }

    function setVABIContract(address _vabiContract) internal {
        IdentitySystemStorage.IdentitySystem storage ds = IdentitySystemStorage.diamondStorage();
        ds.vabbleStorage.vabi = _vabiContract;
    }

    function setInterestRate(uint256 _interestRate) internal {
        IdentitySystemStorage.IdentitySystem storage ds = IdentitySystemStorage.diamondStorage();
        ds.vabbleStorage.interestRate = _interestRate;
    }

    function setVabbToVabiRatio(uint256 _vabbToVabiRatio) internal {
        IdentitySystemStorage.IdentitySystem storage ds = IdentitySystemStorage.diamondStorage();
        ds.vabbleStorage.vabbToVabiRatio = _vabbToVabiRatio;
    }

    function mintInvoiceNFT(address _to, uint256 _tokenId, string memory _metadata) internal {
        IdentitySystemStorage.IdentitySystem storage ds = IdentitySystemStorage.diamondStorage();
        INFT(ds.vabbleStorage.nft).mint(_to, _tokenId, _metadata);
    }

    function depositInvoice(uint256 _tokenId) internal {
        IdentitySystemStorage.IdentitySystem storage ds = IdentitySystemStorage.diamondStorage();
        IInvoicePool(ds.vabbleStorage.invoicePool).depositInvoice(_tokenId);
    }

    function mintVABBTokens(address _to, uint256 _amount) internal {
        IdentitySystemStorage.IdentitySystem storage ds = IdentitySystemStorage.diamondStorage();
        IVABB(ds.vabbleStorage.vabb).mint(_to, _amount);
    }

    function distributeInterest() internal {
        IdentitySystemStorage.IdentitySystem storage ds = IdentitySystemStorage.diamondStorage();
        uint256 interest = IUSDCPool(ds.vabbleStorage.usdcPool).getBalance() * ds.vabbleStorage.interestRate / 10000;
        uint256 invoicePoolInterest = interest / 2;
        uint256 vabiInterest = interest - invoicePoolInterest;

        IUSDCPool(ds.vabbleStorage.usdcPool).withdraw(interest);
        IUSDCPool(ds.vabbleStorage.usdcPool).deposit(invoicePoolInterest);

        uint256 vabiTokensToMint = vabiInterest * ds.vabbleStorage.vabbToVabiRatio;
        IVABI(ds.vabbleStorage.vabi).mint(address(this), vabiTokensToMint);
    }

    function burnInvoiceNFT(uint256 _tokenId) internal {
        IdentitySystemStorage.IdentitySystem storage ds = IdentitySystemStorage.diamondStorage();
        INFT(ds.vabbleStorage.nft).burn(_tokenId);
    }

    function withdrawInvoice(uint256 _tokenId) internal {
        IdentitySystemStorage.IdentitySystem storage ds = IdentitySystemStorage.diamondStorage();
        IInvoicePool(ds.vabbleStorage.invoicePool).withdrawInvoice(_tokenId);
    }

    function getInvoiceCount() internal view returns (uint256) {
        IdentitySystemStorage.IdentitySystem storage ds = IdentitySystemStorage.diamondStorage();
        return IInvoicePool(ds.vabbleStorage.invoicePool).getInvoiceCount();
    }

    function getInvoiceTokenId(uint256 _index) internal view returns (uint256) {
        IdentitySystemStorage.IdentitySystem storage ds = IdentitySystemStorage.diamondStorage();
        return IInvoicePool(ds.vabbleStorage.invoicePool).getInvoiceTokenId(_index);
    }

    function getVABBBalance(address _account) internal view returns (uint256) {
        IdentitySystemStorage.IdentitySystem storage ds = IdentitySystemStorage.diamondStorage();
        return IVABB(ds.vabbleStorage.vabb).balanceOf(_account);
    }

    function getVABIBalance(address _account) internal view returns (uint256) {
        IdentitySystemStorage.IdentitySystem storage ds = IdentitySystemStorage.diamondStorage();
        return IVABI(ds.vabbleStorage.vabi).balanceOf(_account);
    }

    function redeemVABITokens(uint256 _amount) internal {
        IdentitySystemStorage.IdentitySystem storage ds = IdentitySystemStorage.diamondStorage();
        IVABI(ds.vabbleStorage.vabi).burn(msg.sender, _amount);
        uint256 usdcAmount = _amount / ds.vabbleStorage.vabbToVabiRatio;
        IUSDCPool(ds.vabbleStorage.usdcPool).withdraw(usdcAmount);
        payable(msg.sender).transfer(usdcAmount);
    }

    function getInterestRate() internal view returns (uint256) {
        IdentitySystemStorage.IdentitySystem storage ds = IdentitySystemStorage.diamondStorage();
        return ds.vabbleStorage.interestRate;
    }

    function getVabbToVabiRatio() internal view returns (uint256) {
        IdentitySystemStorage.IdentitySystem storage ds = IdentitySystemStorage.diamondStorage();
        return ds.vabbleStorage.vabbToVabiRatio;
    }

    function getInvoiceMetadata(uint256 _tokenId) internal view returns (string memory) {
        IdentitySystemStorage.IdentitySystem storage ds = IdentitySystemStorage.diamondStorage();
        return INFT(ds.vabbleStorage.nft).getMetadata(_tokenId);
    }

    function getInvoiceOwner(uint256 _tokenId) internal view returns (address) {
        IdentitySystemStorage.IdentitySystem storage ds = IdentitySystemStorage.diamondStorage();
        return INFT(ds.vabbleStorage.nft).ownerOf(_tokenId);
    }

    function isInvoiceNFTOwner(uint256 _tokenId, address _account) internal view returns (bool) {
        IdentitySystemStorage.IdentitySystem storage ds = IdentitySystemStorage.diamondStorage();
        return INFT(ds.vabbleStorage.nft).ownerOf(_tokenId) == _account;
    }

    function depositUSDC(uint256 _amount) internal {
        IdentitySystemStorage.IdentitySystem storage ds = IdentitySystemStorage.diamondStorage();
        IUSDCPool(ds.vabbleStorage.usdcPool).deposit(_amount);
    }

    function withdrawUSDC(uint256 _amount) internal {
        IdentitySystemStorage.IdentitySystem storage ds = IdentitySystemStorage.diamondStorage();
        IUSDCPool(ds.vabbleStorage.usdcPool).withdraw(_amount);
    }

    function getUSDCBalance() internal view returns (uint256) {
        IdentitySystemStorage.IdentitySystem storage ds = IdentitySystemStorage.diamondStorage();
        return IUSDCPool(ds.vabbleStorage.usdcPool).getBalance();
    }
}","keccak256":"0x41a28bcda832c82480b123827951f561c0fd79ab1da0306c47e69688deeec357","license":"MIT"},"contracts/utilities/Modifiers.sol":{"content":"// SPDX-License-Identifier: MIT
